        <Square value = {squares[6]} onSquareClick={() => handleClick(6)}/>
      </div>
    </>
  );
}


function calculateWinner(squares) {

  if(checkVertical(squares)){
    return true;
  };
  checkHorizontal(squares);
  checkDiag1(squares);
  checkDiag2(squares);
  return null;
}
